/////////////////////////////////////////////////////////////////////////////
// CSyspalApp initialization

BOOL CSyspalApp::InitInstance()
{
    CFrameWnd* pWnd = new CMainFrame;
    RECT rcWnd;
    rcWnd.top = 0;
    rcWnd.left = ::GetSystemMetrics(SM_CXSCREEN) * 4 / 5;
    rcWnd.bottom = ::GetSystemMetrics(SM_CYSCREEN) / 5;
    rcWnd.right = ::GetSystemMetrics(SM_CXSCREEN);
    pWnd->Create(NULL,
                 "SysPal",
                 WS_OVERLAPPEDWINDOW,
                 rcWnd);
    pWnd->ShowWindow(SW_SHOW);
    m_pMainWnd = pWnd;
    HCURSOR hCur = LoadCursor(IDR_MAINFRAME);
    SetClassLong(pWnd->GetSafeHwnd(), GCW_HCURSOR, (WORD)hCur);
    return TRUE;
}

